import React, { useContext,useEffect ,useReducer} from 'react'
import {CheckoutSteps} from "../components/CheckoutSteps"
import {Row,Col, Card,Button,ListGroup, ListGroupItem} from "react-bootstrap"
import {Store} from "../Store"
import {Link,useNavigate} from "react-router-dom";
import {toast} from "react-toastify"
import getError from '../utils';
import axios from 'axios';


const reducer=(state,action)=>{
    switch(action.type) {
        case "CREATE_REQUEST":
            return {...state,loading:true}
        case "CREATE_SUCCESS":
            return {...state,loading:false}
        case "CREATE_FAIL":
            return {...state,loading:false}
        default:return state;
    }
}

const PlaceOrderScreen = () => {
    const navigate=useNavigate();
    const {state,dispatch:cxtDispatch}=useContext(Store);
    const [{loading},dispatch]=useReducer(reducer,{
        error:"",
        loading:false
    })
    const {cart,userInfo}=state;

    const round2=(num)=>Math.round(num*100+Number.EPSILON)/100;
    cart.itemPrice=round2(cart.cartItems.reduce((a,c)=>a+(c.price*c.quantity),0));
    cart.shippingPrice=cart.itemPrice>100?round2(0):round2(10);
    cart.taxPrice=round2(cart.itemPrice*0.15);
    cart.totalPrice=round2(cart.itemPrice+cart.shippingPrice+cart.taxPrice);
    const placeOrderHandler=async()=>{
            try {
                dispatch({type:"CREATE_REQUEST"});
                const {data}=axios.post('http://localhost:4000/orders',{
                    orderItems:cart.cartItems,
                    shippingAddress:cart.shippingAddress,
                    paymentMethod:cart.paymentMethod,
                    itemsPrice:cart.itemPrice,
                    shippingPrice:cart.shippingPrice,
                    taxPrice:cart.taxPrice,
                    totalPrice:cart.totalPrice
                },
                {headers:{
                    authorization:`Bearer ${userInfo.token}`
                }}
                )
                cxtDispatch({type:"CART_CLEAR"})
                dispatch({type:"CREATE_SUCCESS"});
                localStorage.removeItem("cartItems");
                navigate(`/order/${data.order._id}`)
            }catch(err) {
                toast.error(getError(err));
            }
    }
    useEffect(()=>{
        if(!cart.paymentMethod) {
            navigate('/payment');
        }
    },[navigate,cart.paymentMethod])

  return (
    <div>
      <CheckoutSteps step1 step2 step3 step4/>
      <h1 className="my-3">Preview Order</h1>
      <Row>
          <Col md={8}>
              <Card className="mb-3">
                  <Card.Body>
                      <Card.Title>Shipping</Card.Title>
                      <Card.Text>
                        <strong>Name:</strong>{cart.shippingAddress.fullName}<br/>
                        <strong>Address:</strong> {cart.shippingAddress.address},
                        {cart.shippingAddress.city},{cart.shippingAddress.postalCode},
                        {cart.shippingAddress.country}
                      </Card.Text>
                      <Link to="/shipping">Edit</Link>
                  </Card.Body>
              </Card>
              <Card className="mb-3">
                  <Card.Body>
                      <Card.Title>Payment</Card.Title>
                      <Card.Text>
                          <strong>Method:</strong>
                          {cart.paymentMethod}
                      </Card.Text>
                      <Link to="/payment">Edit</Link>
                  </Card.Body>
              </Card>
              <Card className="mb-3">
                  <Card.Body>
                      <Card.Title>Items</Card.Title>
                        <ListGroup variant="flush">
                            {cart.cartItems.map(item=>(
                            <ListGroupItem>
                                <Row className='align-items-center'>
                                    <Col md={6}>
                                        <img className="img-fluid rounded img-thumbnail" src={item.image} alt={item.name}/>
                                        <Link to={`products/${item.slug}`}>{item.name}</Link>
                                    </Col>
                                    <Col md={3}>{item.quantity}</Col>
                                    <Col md={3}>$ {item.price}</Col>
                                </Row>
                            </ListGroupItem>
                            ))}
                        </ListGroup>
                        <Link to="/cart">Edit</Link>
                  </Card.Body>
              </Card>
          </Col>
          <Col md={4}>
              <Card>
                  <Card.Body>
                      <Card.Title>Order Summary</Card.Title>
                      <ListGroup variant="flush">
                          <ListGroupItem>
                              <Row>
                                  <Col>Items:</Col>
                                  <Col>${cart.itemPrice.toFixed(2)}</Col>
                              </Row>
                          </ListGroupItem>
                          <ListGroupItem>
                              <Row>
                                  <Col>Shipping</Col>
                                  <Col>$ {cart.shippingPrice.toFixed(2)}</Col>
                              </Row>
                          </ListGroupItem>
                          <ListGroupItem>
                              <Row>
                                  <Col>Tax</Col>
                                  <Col>$ {cart.taxPrice.toFixed(2)}</Col>
                              </Row>
                          </ListGroupItem>
                          <ListGroupItem>
                              <Row>
                                  <Col>Order Total</Col>
                                  <Col>$ {cart.totalPrice.toFixed(2)}</Col>
                              </Row>
                          </ListGroupItem>
                          <ListGroupItem>
                              <div className="d-grid">
                                  <Button onClick={placeOrderHandler} type="button" disabled={cart.cartItems.length===0}>Place order</Button>
                              </div>
                          </ListGroupItem>
                      </ListGroup>
                  </Card.Body>
              </Card>
          </Col>
      </Row>
    </div>
  )
}

export default PlaceOrderScreen;
